    var checkboxeSelectAll = document.getElementById('uninstallall');

    for (var i = 0, n = checkboxes.length; i < n; i++) {
      var ee = checkboxes[i];
      if (checkboxeSelectAll.checked) ee.checked = true;else {
        ee.checked = false;
      }
    }
