    });
  }
  /*
     */


  disableWifi(devices) {
    this.http.post(this.url + '/disablewifi', {
      deviceList: devices,
      toggle: false
    }).subscribe(response => {
      console.log(response);
    }, error => {
      this.displayError(error);
    });
  }
  /*
     */


  enableWifi(devices) {
    this.http.post(this.url + '/enablewifi', {
      deviceList: devices,
      toggle: true
    }).subscribe(response => {
      console.log(response);
    }, error => {
      this.displayError(error);
    });
  }
  /*
  */


  forgetAllWifi(devices) {
