export class FoctionnalitesComponent {
    constructor(http) {
        this.http = http;
        this.title = 'FleetControl';
        this.url = "http://localhost:4201";
        this.devices = [];
        this.batteryLevels = [];
        this.devicesListSelection = [];
        this.shellcmd = "";
        this.adbcmd = "";
        this.packageFormData = new FormData(); //used to transfer an apk to be installed
        this.packageNameCheck = "";
        this.packageToUninstall = "";
        this.fileFormData = new FormData(); //used to push files
        this.fileToDelete = "";
        this.fileToPull = "";
        this.wifissid = "";
        this.wifiusername = "";
        this.wifiPasswordType = "WPA";
        this.wifipassword = "";
        this.recordTime = 5;
        this.output = ""; //testing purposes
    }
    ngOnInit() {
    }
    /**
     Selectionner tous les packages à désintaller
     */
    toggle(source) {
        var checkboxes = document.getElementsByName("package");
        var checkboxeSelectAll = document.getElementById('uninstallall');
        for (var i = 0, n = checkboxes.length; i < n; i++) {
            var ee = checkboxes[i];
            if (checkboxeSelectAll.checked)
                ee.checked = true;
            else {
                ee.checked = false;
            }
        }
    }
    /*

    */
    nonDisplayAlert() {
        document.getElementById('alertSuccess').style.display = "none";
        document.getElementById('alertFailed').style.display = "none";
    }
    /*
      Callback used to display any errors when a request doesn't work
    */
    displayError(error) {
        console.error(error.message);
    }
    /*
      Convert {a: 10, b: 20, c: 30} to [a: 10, b: 20, c: 30]
    */
    objectToArray(object) {
        var array = [];
        for (var i in object) {
            array[i] = object[i];
        }
        return array;
    }
    /*
      Converts a 64bit data string into a blob used to download files
    */
    base64ToBlob(b64Data, contentType = '', sliceSize = 512) {
        b64Data = b64Data.replace(/\s/g, ''); //IE compatibility...
        let byteCharacters = atob(b64Data);
        let byteArrays = [];
        for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
            let slice = byteCharacters.slice(offset, offset + sliceSize);
